import mongoose,{Schema} from "mongoose";

const UserSchema=new Schema({
    rol:{   type: String,maxlenght:30,requered:true},
    name:{   type: String,maxlenght:250,requered:true},
    surname:{   type: String,maxlenght:250,requered:true},
    email:{   type: String,maxlenght:250,requered:true, unique:true},
    password:{   type: String,maxlenght:250,requered:true},
    avatar:{   type: String,maxlenght:250,requered:false},
    state:{   type: Number,default:1},//1 es activaod y 2 es desactivado
    phone:{   type: String,maxlenght:250,requered:true},
    birthday:{   type: String,maxlenght:250,requered:false},
},{
    timestamps:true
});
const User  =mongoose.model("user",UserSchema);
export default User;

