sourcesContent:
import jwt from 'jsonwebtoken'
import models from '../models';
export default{
    encode: async(_id,rol,email)=>{
        const token= jwt.sign({_id:_id,rol:rol,email:email},'tesis_token',{expiresIn:'id'});
        return token;

    },
    decode: async(token)=>{
        try{
            const {_id}=await jwt.verify(token,'tesis_token');
            const user =models.User.findOne({_id:_id,status:1});
            if(user){
                return user;
            }
            return false;

        }catch(error){
            console.log(error);
            return false;
        }

    }
}

